    this.name = name;
    this.job = job;
}

//프로토타입으로 메소드 생성
Character.prototype = {
  move: function() {
    console.log(`${this.name} ${this.job} 캐릭터 이동`);
  },
  attack: function() {
    console.log(`${this.name} ${this.job} (이)가 공격`);
  },
  escape: function() {
    console.log(`${this.name} ${this.job} (이)가 도망`);
  },
  dead: function() {
    console.log(`${this.name} ${this.job} (이)가 사망`);
  }
}

//객체 인스턴스
var c1 = new Character('루이스', '기사');

//메소드 실행
c1.move();
c1.attack();
c1.escape();
c1.dead();
```

